// SPDX-License-Identifier: MIT

pragma solidity ^0.7.3;
import "./sloth-verifiable-delay.sol";

contract Sloth_Test_Harness {
  uint256 public y;
  bool public valid;

  function set(uint256 _y) external {
    y = _y;
  }

  function compute_and_store(
    uint256 x,
    uint256 p,
    uint256 i
  ) external {
    y = Sloth_Verifiable_Delay.compute(x, p, i);
  }

  function verify_and_store(
    uint256 x,
    uint256 p,
    uint256 i
  ) external {
    valid = Sloth_Verifiable_Delay.verify(y, x, p, i);
  }
}
